var app=angular.module("myapp",[]);
app.service("mservice",function(){
    this.add=function(x,y)
    {
        return parseInt(x)+parseInt(y);
    }
    this.mul=function(x,y)
    {
        return parseInt(x)*parseInt(y);
    }
    this.sub=function(x,y)
    {
        return parseInt(x)-parseInt(y);
    }
    this.div=function(x,y)
    {
        return parseInt(x)/parseInt(y);
    }
});
app.controller("myctrl",function($scope,mservice)
{
    $scope.x=3;
    $scope.y=4;
    $scope.result=0;
    $scope.caladd=function(){
        $scope.result=mservice.add($scope.x,$scope.y);
    }
    $scope.calmul=function(){
        $scope.result=mservice.mul($scope.x,$scope.y);
    }
    $scope.caldiv=function(){
        $scope.result=mservice.div($scope.x,$scope.y);
    }
    $scope.calsub=function(){
        $scope.result=mservice.sub($scope.x,$scope.y);
    }
});